mapper.select:
        Select scmmaterial.itemNo,scmmaterial.itemName,scmmaterial.spec,scminvstock.lot,scminvstock.unit,scminvstock.qty,
			scminvstock.taxRate,scminvstock.amt,scminvstock.taxAmt,scminvstock.pieUnit,scminvstock.pieQty,
			scminvstock.vendorId,scminvstock.orgUnitNo,scminvstock.wareHouseId,scminvstock.invDate
			from scminvstock,scmmaterial,scmmaterialgroupdetail,scmmaterialgroupstandard
			Where scminvstock.qty > 0 And scminvstock.costCenter=0 and scminvstock.itemId=scmmaterial.id And scmmaterial.id=scmmaterialgroupdetail.itemId
			And scmmaterialgroupdetail.standardId=scmmaterialgroupstandard.id AND scmmaterialgroupstandard.standardType='1'

			    and scminvstock.wareHouseId = #{wareHouseId}


			    and scminvstock.orgUnitNo in ${invOrgUnitNo}


            	and scmmaterialgroupdetail.classId in ${materialClassIds}


			    and scminvstock.itemId = #{materialId}


			    and scminvstock.vendorId = #{vendorId}

        order by scmmaterial.itemNo,scminvstock.lot

select a.finOrgUnitNo, a.costOrgUnitNo, a.costCenter, a.orgUnitNo, a.wareHouseId, a.unit, SUM(a.qty) as qty, SUM(a.amt) as amt,SUM(a.taxAmt) as taxAmt, '2' as level from scminvstock a, scmmaterialgroup b, scmmaterial c, scmmaterialgroupdetail d,scmmaterialgroupstandard where a.itemId = c.id and c.id = d.itemId and b.id = d.classId And d.standardId=scmmaterialgroupstandard.id AND scmmaterialgroupstandard.standardType='1' and a.qty > 0
and a.costCenter = 1
and a.costCenter = 0
and a.itemId = #{materialId}
and b.id in ${materialClassIds}
and a.finOrgUnitNo in ${finOrgUnitNo}
group by a.finOrgUnitNo, a.costOrgUnitNo, a.costCenter, a.orgUnitNo, a.wareHouseId, a.unit order by a.finOrgUnitNo, a.costOrgUnitNo, a.costCenter, a.orgUnitNo, a.wareHouseId, a.unit

SELECT orgUnitNo,wareHouseId,itemNo,itemName,spec,unit,pieUnit,classCode,className,sum(qty) as qty,sum(pieQty) as pieQty,sum(amt) as amt,sum(taxAmt) as taxAmt, sum(addInQty) as addInQty,sum(addInPieQty) as addInPieQty,sum(addInAmt) as addInAmt,sum(addInTaxAmt) as addInTaxAmt, sum(outQty) as outQty,sum(outPieQty) as outPieQty,sum(outAmt) as outAmt,sum(outTaxAmt) as outTaxAmt,sum(qty)+sum(addInQty)-sum(outQty) as allStockQty, sum(pieQty)+sum(addInPieQty)-sum(outPieQty) as allStockPieQty,sum(amt)+sum(addInAmt)-sum(outAmt) as allStockAmt, sum(taxAmt)+sum(addInTaxAmt)-sum(outTaxAmt) as allStockTaxAmt FROM (SELECT A.orgUnitNo,A.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt, SUM(B.Qty) As addInQty,SUM(pieQty) AS addInPieQty,SUM(B.Amt) AS addInAmt, SUM(B.taxAmt) AS addInTaxAmt,0 AS outQty,0 AS outPieQty,0 AS outAmt ,0 AS outTaxAmt FROM ScmInvInitBill A,ScmInvInitBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.initId = B.initId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId And y.itemId=z.id And A.status !='E'
And A.orgUnitNo in (${invOrgUnitNo})
And A.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY A.orgUnitNo,A.wareHouseId,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt, SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.Qty ELSE - B.qty END), SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.pieQty ELSE -B.pieQty END), SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.amt ELSE -B.amt END), SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.taxAmt ELSE -B.taxAmt END),0,0,0,0 FROM ScmInvPurInwarehsBill A,ScmInvPurInwarehsBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.WRID = B.WRID And sysBill='0' And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemId = z.id And A.status !='E'
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.wareHouseID,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt, SUM(B.qty),SUM(pieQty),SUM(B.amt),SUM(B.taxAmt),0,0,0,0 FROM ScmInvMoveInwarehsBill A,ScmInvMoveInwarehsBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.wrId = B.wrId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemId = z.id And A.status !='E'
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.wareHouseId,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt, SUM(B.qty),SUM(pieQty),SUM(B.amt),SUM(B.taxAmt),0,0,0,0 FROM ScmInvOtherInWarehsBill AS A,ScmInvOtherInWarehsBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.wrId = B.wrId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemid = z.id And A.status !='E'
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.wareHouseId,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt,0,0,0,0, SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.qty ELSE - B.qty END), SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.pieQty ELSE -B.pieQty END),SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.taxAmt*(1-B.taxRate) ELSE -B.taxAmt*(1-B.taxRate) END), SUM(CASE WHEN A.bizType IN ('1','2','3') THEN B.taxAmt ELSE -B.taxAmt END) FROM ScmInvSaleIssueBill A,ScmInvSaleIssueBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.otId = B.otId AND A.sysBill = '0' And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemId = z.id And A.status !='E' and B.wareHouseId >0
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.WareHouseId,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT A.orgUnitNo,A.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt,0,0,0,0, SUM(CASE WHEN A.bizType = '1' THEN B.qty ELSE -B.qty END), SUM(CASE WHEN A.bizType = '1' THEN B.pieQty ELSE -B.pieQty END),SUM(CASE WHEN A.bizType = '1' THEN B.amt ELSE -B.amt END), SUM(CASE WHEN A.bizType = '1' THEN B.taxAmt ELSE -B.taxAmt END) FROM ScmInvMaterialReqBill A,ScmInvMaterialReqBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.otId = B.otId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemid = z.id And A.status !='E'
And A.orgUnitNo in (${invOrgUnitNo})
And A.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY A.orgUnitNo,A.wareHouseID,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseID,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt,0,0,0,0, SUM(B.qty),SUM(pieQty),SUM(B.amt),SUM(B.TaxAmt) FROM ScmInvMoveIssueBill A,ScmInvMoveIssueBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.otId = B.otId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId And x.id = y.classId And y.itemId = z.id And A.status !='E'
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.wareHouseID,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT B.orgUnitNo,B.wareHouseId,z.itemNo,z.itemName,z.spec,B.unit,B.pieUnit,x.classCode,x.className,0 as qty,0 as pieQty,0 as amt,0 as taxAmt,0,0,0,0, SUM(B.qty),SUM(pieQty),SUM(B.amt),SUM(B.TaxAmt) FROM ScmInvOtherIssueBill A,ScmInvOtherIssueBillEntry B,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z WHERE A.otId = B.otId And B.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId AND y.itemid = z.id And A.status !='E'
And B.orgUnitNo in (${invOrgUnitNo})
And B.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY B.orgUnitNo,B.wareHouseId,z.itemNo,B.unit,B.pieUnit,x.classCode UNION SELECT A.orgUnitNo,A.wareHouseId,z.itemNo,z.itemName,z.spec,A.unit,A.pieUnit,x.classCode,x.className,sum(A.qty) as qty,sum(A.pieQty) as pieQty,sum(A.amt)as amt,sum(A.taxAmt) as taxAmt,0,0,0,0,0,0,0,0 FROM scminvstock A,ScmMaterialGroupStandard C, ScmMaterialGroup AS x,ScmMaterialGroupDetail AS y,ScmMaterial AS z Where A.costCenter=0 And A.itemId =z.id And C.standardType='1' And C.id= x.standardId AND x.id = y.classId And y.itemId=z.id
And A.orgUnitNo in (${invOrgUnitNo})
And A.wareHouseId = #{wareHouseId}
And x.id in (${materialClassIds})
And Z.id = #{materialId}
GROUP BY A.orgUnitNo,A.wareHouseId,z.itemNo,A.unit,A.pieUnit,x.classCode) AS T GROUP BY orgUnitNo,wareHouseId,itemNo,unit,pieUnit,classCode,className

select B.itemId, C.itemNo, C.itemName, C.Spec, E.className, A.vendorId, G.vendorName, B.unit, H.unitName, B.orgUnitNo, B.wareHouseId,B.useOrgUnitNo, B.taxRate as addInTaxRate, SUM(case when A.bizType in ('1','3') then B.qty else -B.qty end) as addInQty, SUM(case when A.bizType in ('1','3') then B.amt else -B.amt end) as addInAmt, SUM(case when A.bizType in ('1','3') then B.taxAmt else -B.taxAmt end) as addInTaxAmt from ScmInvPurInWarehsBill as A, ScmInvPurInWarehsBillEntry as B,ScmMaterial C, ScmMaterialGroupDetail D,ScmMaterialGroup E,ScmMaterialGroupStandard F, scmsupplier G, scmMeasureUnit H Where A.wrId=B.wrId And B.itemId=C.id And C.id=D.itemId And F.standardType='1' And D.classId=E.id And D.standardId=E.standardId And D.standardId=F.id And A.vendorId = G.id And B.unit = H.id
and B.orgUnitNo in (${orgUnitNo})
and A.bizDate >= #{begDate}
and A.bizDate <= #{endDate}
And E.id in (${materialClassIds})
And B.itemId in (${materialIds})
And A.vendorId in (${vendorIds})
And A.status = 'E'
And B.wareHouseId > 0
And B.wareHouseId = #{wareHouseId}
And IFNULL(B.useOrgUnitNo,'')!=''
And B.useOrgUnitNo = #{useOrgUnitNo}
And B.taxRate = #{taxRate}
group by B.itemId, A.vendorId, B.unit, B.orgUnitNo,B.taxRate,B.wareHouseId,B.useOrgUnitNo
Order By A.vendorId,C.itemNo
Order By C.itemNo,A.vendorId

Select A.itemId,A.unit,A.orgUnitNo,A.saleTaxRate,B.custId, Sum(Case When B.bizType in('1','2','3') Then A.saleQty Else 0 End) as saleQty, Sum(Case When B.bizType in('1','2','3') Then A.saleTaxAmt Else 0 End) as saleTaxAmt, Sum(Case When B.bizType in('1','2','3') Then 0 Else A.saleQty End) as returnQty, Sum(Case When B.bizType in('1','2','3') Then 0 Else A.saleTaxAmt End) as returnTaxAmt from ScmInvSaleissuebillentry A,ScmInvSaleissuebill B,ScmMaterial C, ScmMaterialGroupDetail D,ScmMaterialGroup E,ScmMaterialGroupStandard F WHERE A.otId = B.otId And A.itemId = C.id And C.id=D.itemId And F.standardType='1' And D.classId=E.id And D.standardId=E.standardId And D.standardId=F.id
AND B.bizDate >= #{beginDate}
And B.BizDate <= #{endDate}
AND A.orgUnitNo in (${orgUnitNos})
AND B.custId in (${custIds})
AND (A.wareHouseId in(${whOrDept}) or A.outOrgUnitNo in(${whOrDept}))
AND A.saleTaxRate=#{saleTaxRate}
AND B.status not in('N')
AND B.status='E'
AND A.itemId=#{materialName}
AND E.id in(${materialClassName})
Group By A.itemId,A.unit,A.orgUnitNo,A.saleTaxRate,B.custId ORDER BY B.custId,C.itemNo ASC

		select G.classCode, G.className, A.vendorId, 
			0 as totalAmt,
			0 as taxtotalAmt,
			0 as TaxTotalAmount,
			sum(case when A.biztype < '6' then B.Amt else - B.Amt end) as Amt,  
   			sum(case when A.biztype < '6' then B.TaxAmt else - B.TaxAmt end) as taxAmt,
   			sum(case when A.biztype < '6' then B.TaxAmt - B.Amt else - B.TaxAmt + B.Amt end) as TaxAmount
		from ScmInvPurInWarehsBill as A, ScmInvPurInWarehsBillEntry as B,ScmMaterial C,
		ScmMaterialGroupDetail D,ScmMaterialGroup E,ScmMaterialGroupStandard F,ScmMaterialGroup G
		Where A.wrId=B.wrId And B.itemId=C.id	And C.id=D.itemId And F.standardType='1'
		And D.classId=E.id And D.standardId=E.standardId And D.standardId=F.id

            and B.orgUnitNo = #{orgUnitNo}


            and A.bizDate >= #{begDate}


            and A.bizDate <= #{endDate}


           And E.id = #{materialClassId}


            And A.vendorId = #{vendorId}


             And A.status = 'E'


         	And B.wareHouseId > 0


            And B.wareHouseId = #{wareHouseId}


         	And IFNULL(B.useOrgUnitNo,'')!=''


            And B.useOrgUnitNo = #{useOrgUnitNo}



	    	  	And G.longNo=(Case WHEN (LENGTH(E.longNo) - LENGTH( REPLACE (E.longNo, ',', ''))) < ${longNo} THEN E.longNo ELSE CONCAT(substring_index(E.longNo, ',', ${longNo}),',') End)


        		And G.longNo=E.longNo



            And D.standardId = #{standardName}

		group by G.classCode, G.className, A.vendorId
		Order By A.vendorId

select B.itemId, C.itemNo, C.itemName, C.Spec, E.className,B.remarks, A.wrNo, A.buyerId, A.vendorId, G.vendorName, B.unit, H.unitName, B.orgUnitNo, B.wareHouseId,B.useOrgUnitNo,B.taxRate as addInTaxRate, SUM(case when A.bizType in ('1','3') then B.qty else -B.qty end) as addInQty, SUM(case when A.bizType in ('1','3') then B.amt else -B.amt end) as addInAmt, SUM(case when A.bizType in ('1','3') then B.taxAmt else -B.taxAmt end) as addInTaxAmt, SUM(case when A.bizType in ('1','3') then B.taxAmt else -B.taxAmt end) - SUM(case when A.bizType in ('1','3') then B.amt else -B.amt end) as addInTaxAmount from ScmInvPurInWarehsBill as A, ScmInvPurInWarehsBillEntry as B,ScmMaterial C, ScmMaterialGroupDetail D,ScmMaterialGroup E,ScmMaterialGroupStandard F, scmsupplier G, scmMeasureUnit H Where A.wrId=B.wrId And B.itemId=C.id And C.id=D.itemId And F.standardType='1' And D.classId=E.id And D.standardId=E.standardId And D.standardId=F.id And A.vendorId = G.id And B.unit = H.id
and B.orgUnitNo in (${orgUnitNo})
and A.bizDate >= #{begDate}
and A.bizDate <= #{endDate}
And E.id in (${materialClassIds})
And B.itemId in (${materialIds})
And A.vendorId in (${vendorIds})
And A.status = 'E'
And B.wareHouseId > 0
And B.wareHouseId = #{wareHouseId}
And IFNULL(B.useOrgUnitNo,'')!=''
And B.useOrgUnitNo = #{useOrgUnitNo}
And B.taxRate = #{taxRate}
group by B.itemId, A.wrNo, A.buyerId, A.vendorId, B.unit, B.orgUnitNo, B.wareHouseId,B.useOrgUnitNo,B.taxRate
Order By A.vendorId,C.itemNo,B.wareHouseId,B.useOrgUnitNo
Order By C.itemNo,A.vendorId,B.wareHouseId,B.useOrgUnitNo

select B.itemId, A.vendorId, B.unit, B.orgUnitNo, B.wareHouseId, B.taxRate as addInTaxRate, SUM(case when A.bizType = '5' then B.qty when A.bizType = '6' then -B.qty end ) as addInQty, SUM(case when A.bizType = '5' then B.amt when A.bizType = '6' then -B.amt end ) as addInAmt, SUM(case when A.bizType = '5' then B.taxAmt when A.bizType = '6' then -B.taxAmt end ) as addInTaxAmt from scminvotherinwarehsbill as A, scminvotherinwarehsbillentry as B, scmmaterial C, scmmaterialgroupdetail D, scmmaterialgroup E, scmmaterialgroupstandard F where A.wrId = B.wrId and B.itemId = C.id and C.id = D.itemId and F.standardType = '1' and D.classId = E.id and D.standardId = E.standardId and D.standardId = F.Id
and B.orgUnitNo in (${orgUnitNo})
and A.bizDate >= #{begDate}
and A.bizDate <= #{endDate}
And E.id in (${materialClassIds})
And B.itemId in (${materialIds})
And A.vendorId in (${vendorIds})
And A.status = 'E'
And B.wareHouseId = #{wareHouseId}
And B.taxRate = #{taxRate}
group by B.itemId, A.vendorId, B.unit, B.orgUnitNo, B.wareHouseId, B.taxRate
