SELECT T1.itemId, T1.itemNo, T1.itemName, T1.spec, T1.lot, T1.OrgUnitNo, T1.wareHouseId, T1.unit, T1.qty AS stockQty, T1.taxAmt AS stockAmt, datediff(now(), (CASE ifnull(lastoutdate,'') WHEN '' THEN lastintdate ELSE lastoutdate END)) AS primnessDays, datediff(now(), lastintdate) AS storageAge, T1.qty AS primnessQty FROM (SELECT itemId, scmmaterial.itemNo, scmmaterial.itemName, scmmaterial.spec, lot, OrgUnitNo, finOrgUnitNo, wareHouseId, unit, sum(qty) as qty, sum(taxAmt) as taxAmt, min(invDate) AS lastintdate FROM scminvstock ,scmmaterial WHERE finOrgUnitNo in (${finOrgUnitNo}) AND costCenter=0 AND scminvstock.itemId=scmmaterial.id
AND scminvstock.OrgUnitNo in (${invOrgUnitNo}) AND scminvstock.qty > 0
AND scminvstock.wareHouseId = #{wareHouseId}
AND itemId = #{materialId}
AND lot = #{lot}
group by itemId, scmmaterial.itemNo, scmmaterial.itemName, scmmaterial.spec, lot, OrgUnitNo, finOrgUnitNo, wareHouseId, unit) T1 LEFT JOIN ( SELECT itemId,lot,finOrgUnitNo,wareHouseId,MAX(lastoutdate) AS lastoutdate FROM ( SELECT b.itemId,b.lot,b.finOrgUnitNo,b.wareHouseId,max(a.bizdate) AS lastoutdate FROM scminvpurinwarehsbill a,scminvpurinwarehsbillentry b,scminvstock c WHERE a.wrid=b.wrId AND b.itemId=c.itemId AND b.lot=c.lot AND a.finOrgUnitNo=c.finOrgUnitNo AND a.status='E' AND b.wareHouseId=c.wareHouseId AND c.qty > 0
And b.wareHouseId = #{wareHouseId}
AND b.itemId = #{materialId}
AND b.lot = #{lot}
AND a.bizType > 5 AND c.finOrgUnitNo in (${finOrgUnitNo}) AND c.costCenter=0 GROUP BY b.finOrgUnitNo,b.wareHouseId,b.itemId,b.lot UNION SELECT b.itemId,b.lot,b.OrgUnitNo,b.wareHouseId,max(a.bizdate) AS lastoutdate FROM scminvsaleissuebill a,scminvsaleissuebillentry b,scminvstock c WHERE a.otId=b.otId AND b.itemId=c.itemId AND b.lot=c.lot AND a.finOrgUnitNo=c.finOrgUnitNo AND a.status='E' AND b.wareHouseId=c.wareHouseId AND c.qty > 0
And b.wareHouseId = #{wareHouseId}
AND b.itemId = #{materialId}
AND b.lot = #{lot}
AND a.bizType < 6 AND c.finOrgUnitNo in (${finOrgUnitNo}) AND c.costCenter=0 GROUP BY b.OrgUnitNo,b.wareHouseId,b.itemId,b.lot UNION SELECT b.itemId,b.lot,a.finOrgUnitNo,a.wareHouseId,max(a.bizdate) AS lastoutdate FROM ScmInvMaterialReqBill a,scminvmaterialreqbillentry b,scminvstock c WHERE a.otId=b.otId AND b.itemId=c.itemId AND b.lot=c.lot AND a.finOrgUnitNo=c.finOrgUnitNo AND a.status='E' AND a.wareHouseId=c.wareHouseId AND c.qty > 0
AND a.wareHouseId = #{wareHouseId}
AND b.itemId = #{materialId}
AND b.lot = #{lot}
AND a.bizType =1 AND c.finOrgUnitNo in (${finOrgUnitNo}) AND c.costCenter=0 GROUP BY a.finOrgUnitNo,a.wareHouseId,b.itemId,b.lot UNION SELECT b.itemId,b.lot,a.finOrgUnitNo,a.wareHouseId,max(a.bizdate) AS lastoutdate FROM ScmInvMoveIssueBill a,scminvmoveissuebillentry b,scminvstock c WHERE a.otId=b.otId AND b.itemId=c.itemId AND b.lot=c.lot AND a.finOrgUnitNo=c.finOrgUnitNo AND a.status='E' AND a.wareHouseId=c.wareHouseId AND c.qty > 0
AND a.wareHouseId = #{wareHouseId}
AND b.itemId = #{materialId}
AND b.lot = #{lot}
AND c.finOrgUnitNo in (${finOrgUnitNo}) AND c.costCenter=0 GROUP BY a.finOrgUnitNo,a.wareHouseId,b.itemId,b.lot UNION SELECT b.itemId,b.lot,a.finOrgUnitNo,a.wareHouseId,max(a.bizdate) AS lastoutdate FROM ScmInvOtherIssueBill a,scminvotherissuebillentry b,scminvstock c WHERE a.otId=b.otId AND b.itemId=c.itemId AND b.lot=c.lot AND a.finOrgUnitNo=c.finOrgUnitNo AND a.status='E' AND a.wareHouseId=c.wareHouseId AND c.qty > 0
AND a.wareHouseId = #{wareHouseId}
AND b.itemId = #{materialId}
AND b.lot = #{lot}
AND c.finOrgUnitNo in (${finOrgUnitNo}) AND c.costCenter=0 GROUP BY a.finOrgUnitNo,a.wareHouseId,b.itemId,b.lot)T2 GROUP BY itemId,lot,finOrgUnitNo,wareHouseId) T3 ON T1.itemId=T3.itemId AND T1.lot=T3.lot AND T1.finOrgUnitNo=T3.finOrgUnitNo AND T1.wareHouseId=T3.wareHouseId WHERE 1 = 1
AND datediff(now(), (CASE ifnull(lastoutdate,'') WHEN '' THEN lastintdate ELSE lastoutdate END)) >= #{primnessDays}
AND datediff(now(), lastintdate) >= #{storageAge}
ORDER BY T1.itemId

SELECT entry.itemId,mal.itemName,mal.itemNo,entry.saleTaxPrice,entry.unit as saleUnitId, relation.Convrate as saleConvrate , un.UnitName as saleUnitName,mal.spec ,mg.className as itemClassName,mg.classCode as itemClassNo FROM ScmInvSalePriceEntry entry LEFT JOIN ScmInvSalePrice bill on bill.pmid= entry.pmid LEFT JOIN ScmMaterialUnitRelation relation on relation.itemid=entry.itemid and entry.unit = relation.targetUnitId LEFT JOIN ScmMaterial mal ON entry.ItemId = mal.Id LEFT JOIN ScmMaterialGroupDetail mgd ON mgd.ItemId = mal.Id LEFT JOIN ScmMaterialGroup mg ON mgd.classId = mg.id left join ScmMaterialGroupStandard on mgd.standardId = ScmMaterialGroupStandard.id LEFT JOIN ScmMeasureUnit un on entry.unit = un.id where entry.id in ( SELECT max(id) from ScmInvSalePriceEntry b LEFT JOIN ScmInvSalePrice a on b.pmid=a.pmid WHERE b.rowStatus ='E' and a.orgUnitNo = #{orgUnitNo} GROUP BY b.ItemId,a.orgUnitNo ) and bill.orgUnitNo = #{orgUnitNo} and entry.rowStatus ='E' and ScmMaterialGroupStandard.standardType='1'
And entry.itemId in (${materialIds})
And mg.id in (${materialClassIds})
and bill.begDate <= #{businessDate}
and bill.endDate >= #{businessDate}

SELECT entry.itemId,entry.price as putTaxPrice,entry.purUnit as purUnitId ,relation.convrate as purConvrate,un.unitName as purUnitName FROM ScmPurPriceEntry entry LEFT JOIN ScmPurPrice bill on entry.pmId=bill.id LEFT JOIN ScmMaterialUnitRelation relation on relation.itemid=entry.itemid and entry.purunit = relation.targetUnitId LEFT JOIN ScmMeasureUnit un on entry.purunit = un.id WHERE entry.id in ( SELECT max(B.ID) FROM ScmPurPriceEntry b LEFT JOIN ScmPurPrice a on b.pmid=a.id WHERE b.rowStatus='E' and a.orgUnitNo=#{orgUnitNo}
And b.itemId in (${materialIds})
GROUP BY b.ItemId,a.orgUnitNo ) And entry.rowStatus='E' And bill.orgUnitNo=#{orgUnitNo}
And entry.itemId in (${materialIds})
and bill.begDate <= #{businessDate}
and bill.endDate >= #{businessDate}
union all
SELECT b.itemId, price as putTaxPrice,b.purUnit as purUnitId, relation.convrate as purConvrate,un.unitName as purUnitName FROM ScmPurQuotationEntry b LEFT JOIN ScmPurQuotation a on b.pqid = a.id LEFT JOIN ScmMaterialUnitRelation relation on relation.itemid=b.itemid and b.purUnit = relation.targetUnitId LEFT JOIN ScmMeasureUnit un on b.purUnit = un.id where NOT EXISTS(SELECT bb.id FROM ScmPurPriceEntry bb LEFT JOIN ScmPurPrice aa on bb.pmid=aa.id WHERE bb.itemid = b.itemid and bb.rowStatus='E' and aa.orgUnitNo=#{orgUnitNo} GROUP BY bb.ItemId,aa.orgUnitNo) and b.id in ( SELECT max(bbbb.ID) FROM ScmPurQuotationEntry bbbb LEFT JOIN ScmPurQuotation aaaa on bbbb.pqid=aaaa.id WHERE bbbb.rowStatus='E' and aaaa.orgUnitNo=#{orgUnitNo}
And bbbb.itemId in (${materialIds})
GROUP BY bbbb.ItemId ) And b.rowStatus='E' And a.orgUnitNo=#{orgUnitNo}
And b.itemId in (${materialIds})
and a.begDate <= #{businessDate}
and a.endDate >= #{businessDate}

SELECT vendor.vendorNo as vendorCode,VendorName, IFNULL(sum(a.puramt),0) AS puramt,IFNULL(sum(a.purTaxAmt),0) as purTaxAmt,IFNULL(sum(a.receiveAmt),0) as receiveAmt ,IFNULL(sum(a.receiveTaxAmt),0) as receiveTaxAmt FROM (
SELECT bill.vendorid,sum(entry.amt)as purAmt,sum(entry.taxamt)as purTaxAmt,0 as receiveAmt,0 as receiveTaxAmt
FROM scmpurorderentry entry LEFT JOIN scmpurorder bill on entry.poid=bill.id WHERE 1=1
and bill.orderDate >=#{begDate}
and bill.orderDate <=#{endDate}
and bill.vendorid in (${vendor})
and orgunitno in(${orgunitno}) GROUP BY bill.vendorid
union all
SELECT bill2.vendorid,0 as purAmt,0 as purTaxAmt,sum(entry2.amt)as receiveAmt,sum(entry2.taxamt)as receiveTaxAmt FROM scmpurreceiveentry entry2 LEFT JOIN scmpurreceive bill2 on entry2.pvid=bill2.id WHERE 1=1
and bill2.receiveDate >=#{begDate}
and bill2.receiveDate <=#{endDate}
and bill2.vendorid in (${vendor})
and bill2.purOrgUnitNo in(${orgunitno}) GROUP BY bill2.vendorid ) as a LEFT JOIN ScmSupplier vendor on a.vendorId = vendor.id GROUP BY a.vendorId
